import { useState, useEffect, useRef } from "react";

interface AutoPlayAudioProps {
  audioUrl: string;
}
const AutoPlayAudio = ({ audioUrl }: AutoPlayAudioProps) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const audioContextRef = useRef<AudioContext | null>(null);

  useEffect(() => {
    const setupAudio = async () => {
      try {
        // Create video element that can autoplay
        if (videoRef.current) {
          await videoRef.current.play();

          // Create audio context
          audioContextRef.current = new AudioContext();

          // Create audio element and connect it
          const audio = new Audio(audioUrl);
          const source =
            audioContextRef.current.createMediaElementSource(audio);
          source.connect(audioContextRef.current.destination);

          // Play the audio
          await audio.play();
        }
      } catch (err) {
        console.error("Playback failed:", err);
      }
    };

    setupAudio();

    return () => {
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, [audioUrl]);

  return (
    <div className="hidden">
      <video ref={videoRef} muted autoPlay playsInline loop>
        {/* Using a tiny valid video file */}
        <source src="data:video/mp4;base64," />
      </video>
    </div>
  );
};

export default AutoPlayAudio;
